inputs.script:
export DEPLOYMENT_GROUP_NAME=$(Release.EnvironmentName)
export APPLICATION_NAME=$(ApplicationName)
export DT_TAGS=DEPLOYMENT_GROUP_NAME=$(Release.EnvironmentName)
export DT_CUSTOM_PROP="DEPLOYMENT_ID=$(DeploymentId) DEPLOYMENT_GROUP_NAME=$(Release.EnvironmentName) APPLICATION_NAME=$(ApplicationName)"
export DT_CLUSTER_ID="$(Release.EnvironmentName) $(ApplicationName)"
env
sudo kill -9 `ps -ef | grep pm2 | grep -v grep | awk '{print $2}'`

# now we launch our app
cd /home/unbreakablepipeline/dynatrace/app
sudo DT_TAGS=DEPLOYMENT_GROUP_NAME=$(Release.EnvironmentName) DEPLOYMENT_GROUP_NAME=$(Release.EnvironmentName) DT_CLUSTER_ID="$(Release.EnvironmentName) $(ApplicationName)" BUILD_NUMBER=$(SimulateBuildNumber) pm2 start --no-pmx --update-env app.js

echo "Deploying DEPLOYMENT_ID="  $(DeploymentId)
echo "DEPLOYMENT_GROUP_NAME="  $(Release.EnvironmentName) 
echo "APPLICATION_NAME="$(ApplicationName)
echo "DT-Tags=" $DT_TAGS
echo "DT_CLUSTER_ID="$DT_CLUSTER_ID
echo "BUILD_NUMBER="$(SimulateBuildNumber)


# now lets make sure the app is really up & running - execute a couple of requests using the x-dynatrace header to identify these requests in Dynatrace as startup tests
sleep 5;
echo "Running a simply set of curls to validate the service is up and running"
curl -s "http://localhost:8080/" -H "x-dynatrace: NA=StartUp.Homepage;" -o nul &> startuptest.log
curl -s "http://localhost:8080/version" -H "x-dynatrace: NA=StartUp.Version;" -o nul &> startuptest.log
curl -s "http://localhost:8080/api/echo?text=This is from a testing script" -H "x-dynatrace: NA=StartUp.Echo;" -o nul &> startuptest.log
curl -s "http://localhost:8080/api/invoke?url=http://www.dynatrace.com" -H "x-dynatrace: NA=StartUp.Invoke;" -o nul &> startuptest.log
sleep 5;
exit 0

export DEPLOYMENT_GROUP_NAME=$(Release.EnvironmentName)
sudo chmod 777 /home/unbreakablepipeline/dynatrace/start_loadtest.sh
sed -i -e 's/\r$//' /home/unbreakablepipeline/dynatrace/start_loadtest.sh
sudo DEPLOYMENT_GROUP_NAME=$DEPLOYMENT_GROUP_NAME nohup /home/unbreakablepipeline/dynatrace/start_loadtest.sh &>/dev/null &
sleep 300;

#!/bin/sh
postData=$(cat <<EOF
    {
        "eventType" : "CUSTOM_DEPLOYMENT",
        "source" : "AzureDevops" ,
        "deploymentName" : "$(Release.DefinitionName)",
        "deploymentVersion" : "$(Release.ReleaseId)"  ,
        "deploymentProject" : "$(System.TeamProject)" ,
        "ciBackLink" : "$(System.TeamFoundationCollectionUri)",
        "attachRules" : {
               "tagRule" : [
                   {
                       "meTypes":"SERVICE" ,
                   "tags" : [
                       {
                           "context" : "ENVIRONMENT",
                           "key": "DEPLOYMENT_GROUP_NAME",
                           "value" : "Staging"    
                       }
                       ]
                   }
                   ]
        }
}
EOF
)
echo "$postData"
echo  $(Dynatrace_Tenant)"/api/v1/events"
Dynatrace_APIURL="$(Dynatrace_Tenant)"
Dynatrace_APIURL="$Dynatrace_APIURL/api/v1/events"
echo "$Dynatrace_APIURL"
curl --url "$Dynatrace_APIURL" -H "Content-type: application/json" -H "Authorization: Api-Token "$(Dynatrace_API_TOKEN) -X POST -d  "$postData"

export DEPLOYMENT_GROUP_NAME=$(Release.EnvironmentName)
export APPLICATION_NAME=$(ApplicationName)
