export DT_CUSTOM_PROP="DEPLOYMENT_ID=$(DeploymentId) DEPLOYMENT_GROUP_NAME=$(Release.EnvironmentName) APPLICATION_NAME=$(ApplicationName)"
export DT_CLUSTER_ID="$(Release.EnvironmentName) $(ApplicationName)"
env
sudo kill -9 `ps -ef | grep pm2 | grep -v grep | awk '{print $2}'`

